pragma solidity ^0.5.16;

import "../libraries/math/SafeMath.sol";
import "../libraries/tokens/IERC20.sol";
import "../libraries/utils/Ownable.sol";
import "../libraries/utils/Address.sol";
import "../gov/Treasury.sol";

contract LiquidityDistributor is Ownable {
    using SafeMath for uint256;

    event Deposit(
        address indexed depositor,
        address indexed beneficiary,
        uint256 amount
    );
    event Withdraw(
        address indexed depositor,
        address indexed beneficiary,
        uint256 amount
    );
    event EmergencyWithdraw(
        address indexed depositor,
        address indexed beneficiary,
        uint256 amount
    );

    // Info of each user
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has deposited.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of LODs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accLodPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accLodPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each user that deposits LP tokens.
    mapping(address => UserInfo) public userInfo;

    // Info of pool
    IERC20 public depositToken; // Token that users deposit.
    IERC20 public lod; // Token to be distributed.
    Treasury public treasury; // The block number when LOD mining starts.

    //variables
    uint256 public accLodPerShare = 0; // Accumulated LODs per share.
    uint256 public lastRewardBlock; // Last block number that LODs distribution occurs.

    constructor(
        IERC20 _depositToken,
        IERC20 _lod,
        Treasury _treasury
    ) public {
        depositToken = _depositToken;
        lod = _lod;
        treasury = _treasury;
    }

    /**
     * @dev Transfers amount of deposit tokens from the caller.
     * @param _depositor User address who deposits tokens.
     * @param _beneficiary User address who gains credit for this deposit operation.
     * @param _amount Number of tokens to deposit.
     */
    function deposit(
        address _depositor,
        address _beneficiary,
        uint256 _amount
    ) external {
        UserInfo storage user = userInfo[_depositor];
        updatePool();
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(accLodPerShare).sub(
                user.rewardDebt
            );
            if (pending > 0) {
                safeLodTransfer(_beneficiary, pending);
            }
        }

        if (_amount > 0) {
            depositToken.transferFrom(
                address(_depositor),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(accLodPerShare);
        emit Deposit(_depositor, _beneficiary, _amount);
    }

    /**
     * @dev withdraws a certain amount of previously deposited tokens. User also receives their
     * alotted number of distribution tokens.
     * @param _beneficiary User address who gains credit for this deposit operation.
     * @param _amount Number of tokens to withdraw.
     */
    function withdraw(address _beneficiary, uint256 _amount) external {
        UserInfo storage user = userInfo[msg.sender];
        require(
            user.amount >= _amount,
            "cannot withdraw more than your deposit"
        );
        updatePool();
        uint256 pending = user.amount.mul(accLodPerShare).sub(user.rewardDebt);
        if (pending > 0) {
            safeLodTransfer(_beneficiary, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            depositToken.transfer(address(_beneficiary), _amount);
        }
        user.rewardDebt = user.amount.mul(accLodPerShare);
        emit Withdraw(msg.sender, _beneficiary, _amount);
    }

    // Update reward variables of the pool to be up-to-date.
    function updatePool() public {
        if (block.number <= lastRewardBlock) {
            return;
        }
        uint256 totalDepositToken = depositToken.balanceOf(address(this));
        if (totalDepositToken == 0) {
            lastRewardBlock = block.number;
            return;
        }
        uint256 drip = treasury.drip();
        accLodPerShare = accLodPerShare.add(drip.div(totalDepositToken));
        lastRewardBlock = block.number;
    }

    // View function to see pending LODs on frontend.
    function pendingRewards(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 totalDepositToken = depositToken.balanceOf(address(this));
        uint256 _accLodPerShare = accLodPerShare;
        if (block.number > lastRewardBlock && totalDepositToken != 0) {
            uint256 pendingDrip = treasury.pendingDrip(address(this));
            _accLodPerShare = _accLodPerShare.add(
                pendingDrip.div(totalDepositToken)
            );
        }
        return user.amount.mul(_accLodPerShare).sub(user.rewardDebt);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(address _beneficiary) public {
        UserInfo storage user = userInfo[msg.sender];
        depositToken.transfer(address(_beneficiary), user.amount);
        emit EmergencyWithdraw(msg.sender, _beneficiary, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe lod transfer function, just in case if rounding error causes pool to not have enough LODs.
    function safeLodTransfer(address _to, uint256 _amount) internal {
        uint256 lodBal = lod.balanceOf(address(this));
        if (_amount > lodBal) {
            lod.transfer(_to, lodBal);
        } else {
            lod.transfer(_to, _amount);
        }
    }
}

