pragma solidity ^0.5.16;

import "../libraries/tokens/IERC20.sol";
import "../libraries/utils/Ownable.sol";
import "../libraries/math/SafeMath.sol";
import "../libraries/utils/Address.sol";
import "../gov/Treasury.sol";

contract MarketDistributor is Ownable {
    using SafeMath for uint256;
    using Address for address;

    //Constant
    Treasury public treasury;
    IERC20 public token;

    //Variables
    uint256 public span; //17280
    uint256 public round;
    uint256 public lastRewardBlock;
    address public market;
    mapping(uint256 => uint256) public totalReward; //round=>reward
    mapping(uint256 => uint256) public totalRewardShare; //round=>total score
    mapping(uint256 => mapping(address => uint256)) public rewardShares; //round=>address=>scoreshare

    event Registered(address indexed user, uint256 round, uint256 amount);
    event RoundSettled(
        uint256 indexed round,
        uint256 amount,
        uint256 blocknumber
    );
    event Withdraw(address indexed user, uint256 round, uint256 amount);

    constructor(
        uint256 _start,
        uint256 _span,
        IERC20 _token,
        Treasury _treasury
    ) public {
        lastRewardBlock = _start;
        span = _span;
        token = _token;
        treasury = _treasury;
        round += 1;
    }

    function registerVolume(uint256 _volume, address _receipant) external {
        require(msg.sender == market, "INVALID_CALLER");
        changeRound();
        totalRewardShare[round] += _volume;
        rewardShares[round][_receipant] += _volume;
        emit Registered(_receipant, round, _volume);
    }

    // In case gas is too high, use 'withdrawReward' for each round
    function withdrawRewardAll(uint256[] calldata _rounds, address _shareholder)
        external
    {
        for (uint256 i = 0; i < _rounds.length; ++i) {
            withdrawReward(_rounds[i], _shareholder);
        }
    }

    // Gas safe withdraw
    function withdrawReward(uint256 _round, address _shareholder) public {
        changeRound();
        uint256 _reward = checkReward(_round, _shareholder);
        totalRewardShare[_round] -= rewardShares[_round][_shareholder];
        rewardShares[_round][_shareholder] = 0;
        totalReward[_round] -= _reward;
        token.transfer(_shareholder, _reward);
        emit Withdraw(_shareholder, _round, _reward);
    }

    function changeRound() public {
        uint256 blockElapsed = block.number - lastRewardBlock;
        if (blockElapsed >= span) {
            totalReward[round] = treasury.drip();
            round += 1;
            lastRewardBlock = block.number;
            emit RoundSettled(round, totalReward[round], lastRewardBlock);
        }
    }

    function checkReward(uint256 _round, address _shareholder)
        public
        view
        returns (uint256)
    {
        require(round > _round, "INVALID_ROUND");
        if (totalRewardShare[_round] > 0) {
            return
                totalReward[_round].mul(rewardShares[_round][_shareholder]).div(
                    totalRewardShare[_round]
                );
        }
    }

    function setMarket(address _market) external onlyOwner {
        market = _market;
    }
}
