pragma solidity ^0.5.16;

import "../libraries/tokens/ERC20.sol";
import "../libraries/tokens/IERC721.sol";
import "../libraries/utils/Ownable.sol";
import "../libraries/math/SafeMath.sol";
import "../gov/Treasury.sol";

contract StakingDistributor is Ownable, ERC20 {
    using SafeMath for uint256;

    // @notice EIP-20 token name for this token
    string public constant name = "StakingDistributor";

    /// @notice EIP-20 token symbol for this token
    string public constant symbol = "xLOD";

    //Constant
    IERC20 public lod;
    Treasury public treasury;
    uint256 public startBlock;

    //Variables (The term Staked mean to be The Amount of Staked LOD)
    uint256 public totalStaked;
    uint256 public availableReward;
    uint256 public drippedPerShare;
    uint256 public lastRewardBlock;

    struct UserInfo {
        uint256 amount;
        uint256 rewardDebt;
    }

    mapping(address => UserInfo) public userInfo;

    event Deposit(address from, address indexed user, uint256 amount);
    event Withdraw(address from, address indexed user, uint256 amount);

    constructor(IERC20 _lod, Treasury _treasury) public {
        lod = _lod;
        treasury = _treasury;
    }

    function deposit(
        address _depositor,
        address _beneficiary,
        uint256 _amount
    ) public {
        //deposit LOD
        UserInfo storage user = userInfo[_beneficiary];
        updatePool();

        if (user.amount > 0) {
            uint256 pending = user.amount.mul(drippedPerShare).sub(
                user.rewardDebt
            );
            if (pending > 0) {
                safeLodTransfer(_beneficiary, pending);
            }
        }
        if (_amount > 0) {
            lod.transferFrom(_depositor, address(this), _amount);
            user.amount = user.amount.add(_amount);
            totalStaked = totalStaked.add(_amount);
        }
        user.rewardDebt = user.amount.mul(drippedPerShare);
        emit Deposit(_depositor, _beneficiary, _amount);

        //mint xLOD
        _mint(_beneficiary, _amount);
    }

    function withdraw(address _beneficiary, uint256 _amount) public {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        //Burn xLOD
        _burn(msg.sender, _amount);
        updatePool();

        //withdraw LOD and bonus
        uint256 pending = user.amount.mul(drippedPerShare).sub(user.rewardDebt); //bonus LOD
        if (pending > 0) {
            safeLodTransfer(_beneficiary, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            totalStaked = totalStaked.sub(_amount);
            lod.transfer(_beneficiary, _amount);
        }
        user.rewardDebt = user.amount.mul(drippedPerShare);
        emit Withdraw(msg.sender, _beneficiary, _amount);
    }

    function updatePool() public {
        if (block.number <= lastRewardBlock) {
            return;
        }
        if (totalStaked == 0) {
            lastRewardBlock = block.number;
            return;
        }
        uint256 reward = treasury.drip();
        availableReward = availableReward.add(reward);
        drippedPerShare = drippedPerShare.add(reward.div(totalStaked));
        lastRewardBlock = block.number;
    }

    // View function to see pending LODs on frontend.
    function pendingLod(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 _drippedPerShare = drippedPerShare;
        if (block.number > lastRewardBlock && totalStaked != 0) {
            uint256 reward = treasury.pendingDrip(address(this));
            _drippedPerShare = _drippedPerShare.add(reward.div(totalStaked));
        }
        return user.amount.mul(_drippedPerShare).sub(user.rewardDebt);
    }

    // Safe lod transfer function, just in case if rounding error causes pool to not have enough LODs.
    function safeLodTransfer(address _to, uint256 _amount) internal {
        if (_amount > availableReward) {
            uint256 _availableReward = availableReward;
            availableReward = 0;
            lod.transfer(_to, _availableReward);
        } else {
            availableReward = availableReward.sub(_amount);
            lod.transfer(_to, _amount);
        }
    }
}
