pragma solidity ^0.5.16;

import "../libraries/tokens/IERC20.sol";
import "../libraries/utils/Ownable.sol";
import "../libraries/math/SafeMath.sol";
import "../libraries/utils/Address.sol";

/**
 * @title Treasury Contract
 */
contract Treasury is Ownable {
    using SafeMath for uint256;
    using Address for address;

    /// @notice The block number when the Reservoir started (immutable)
    uint256 public dripStart;
    /// @notice Initial tokens per block that to drip to target (immutable)
    uint256 public dripRate;
    /// @notice After a discount span passed, the discount rate difined bwelow get applied to the drip rate(immutable)
    uint256 public discountSpan;
    /// @notice Discount rate for dynamic drip rate(immutable)
    uint256 public discountRate;
    /// @notice Reference to token to drip (immutable)
    IERC20 public token;
    /// @notice round represents how many times discount gets applied to driprate. (round -1)times applied
    uint256 public round;
    /// @notice Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint;
    /// @notice Last block number that LODs distribution occurs.
    uint256 public lastRewardBlock;

    /// @notice Struct for each distribution logic
    struct Info {
        uint256 allocPoint; // How many allocation points assigned to. LODs to distribute per block.
        uint256 accrued; // Accumulated LODs
        bool existance; //If pool exists, it becomes true.
    }
    /// @notice Distributor list
    address[] public distributorList;
    /// @notice Defining each address of distributor
    mapping(address => Info) public distributors;

    /**
     * @notice Constructs a Treasruty
     * @param _dripRate Numer of tokens per block to drip
     * @param _token The token to drip
     */
    constructor(
        uint256 _dripStart,
        uint256 _dripRate,
        uint256 _dripDiscount,
        uint256 _discountSpan,
        IERC20 _token
    ) public {
        dripStart = _dripStart;
        lastRewardBlock = _dripStart;
        dripRate = _dripRate;
        discountRate = _dripDiscount;
        discountSpan = _discountSpan;
        token = _token;
        round = 1;
    }

    /**
     * @notice update reward variables for all distributors.
     */
    function updateAll() public {
        if (block.number >= dripStart) {
            uint256 _spanEnd = round.mul(discountSpan).add(dripStart);
            uint256 _dripTotal;
            if (block.number > _spanEnd) {
                uint256 _previousRate = safeSub(
                    dripRate,
                    discountRate.mul(round - 1)
                );
                uint256 _currentRate = safeSub(
                    dripRate,
                    discountRate.mul(round)
                );
                uint256 _previousTarget = _spanEnd.sub(lastRewardBlock);
                uint256 _currentTarget = block.number.sub(_spanEnd);
                uint256 _previousReward = _previousTarget.mul(_previousRate);
                uint256 _currentReward = _currentTarget.mul(_currentRate);
                _dripTotal = _previousReward.add(_currentReward);
                round += 1;
            } else {
                uint256 _rate = safeSub(dripRate, discountRate.mul(round - 1));
                uint256 _target = block.number.sub(lastRewardBlock);
                uint256 _reward = _target.mul(_rate);
                _dripTotal = _reward;
            }
            uint256 _tokenBalance = token.balanceOf(address(this));
            uint256 _toDrip = min(_tokenBalance, _dripTotal);

            // Finally, write new `dripped` value and accrued drip to target distributor
            if (_toDrip > 0) {
                for (uint256 i = 0; i < distributorList.length; ++i) {
                    address distributor = distributorList[i];
                    uint256 accurued = distributors[distributor].accrued;
                    uint256 reward = _toDrip
                        .mul(distributors[distributor].allocPoint)
                        .div(totalAllocPoint);
                    distributors[distributor].accrued = reward.add(accurued);
                }
                lastRewardBlock = block.number;
            }
        }
    }

    /**
     * @notice Drips the maximum amount of tokens to match the drip rate since inception
     * @dev Note: this will only drip up to the amount of tokens available.
     * @return The amount of tokens dripped in this call
     */
    function drip() public returns (uint256) {
        // Check wheter caller is registered
        require(distributors[msg.sender].existance == true, "INVALID_CALLER");
        updateAll();
        uint256 accrued = distributors[msg.sender].accrued;
        distributors[msg.sender].accrued = 0;
        token.transfer(msg.sender, accrued);
        return accrued;
    }

    /**
     * @notice Check how much can be dripped for a distributor
     * @return The amount of tokens can be dripped for a distributor
     */
    function pendingDrip(address _distributor) public view returns (uint256) {
        if (block.number >= dripStart) {
            uint256 _spanEnd = round.mul(discountSpan).add(dripStart);
            uint256 _dripTotal;
            if (block.number > _spanEnd) {
                uint256 _previousRate = safeSub(
                    dripRate,
                    discountRate.mul(round - 1)
                );
                uint256 _currentRate = safeSub(
                    dripRate,
                    discountRate.mul(round)
                );
                uint256 _previousTarget = _spanEnd.sub(lastRewardBlock);
                uint256 _currentTarget = block.number.sub(_spanEnd);
                uint256 _previousReward = _previousTarget.mul(_previousRate);
                uint256 _currentReward = _currentTarget.mul(_currentRate);
                _dripTotal = _previousReward.add(_currentReward);
            } else {
                uint256 _rate = safeSub(dripRate, discountRate.mul(round - 1));
                uint256 _target = block.number.sub(lastRewardBlock);
                uint256 _reward = _target.mul(_rate);
                _dripTotal = _reward;
            }

            uint256 _tokenBalance = token.balanceOf(address(this));
            uint256 _toDrip = min(_tokenBalance, _dripTotal);
            uint256 _accrued = distributors[_distributor].accrued;
            uint256 _reward = _toDrip
                .mul(distributors[_distributor].allocPoint)
                .div(totalAllocPoint);
            return _reward + _accrued;
        } else {
            return 0;
        }
    }

    /**
     * @notice Add a new distributor to the pool. Can only be called by the owner
     */
    function add(uint256 _allocPoint, address _distributor) public onlyOwner {
        require(distributors[_distributor].existance != true, "DUPLICATED");
        updateAll();
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        distributorList.push(_distributor);
        distributors[_distributor].allocPoint = _allocPoint;
        distributors[_distributor].accrued = 0;
        distributors[_distributor].existance = true;
    }

    /**
     * @notice Update the given distributor's LODs allocation point. Can only be called by the owner.
     */
    function set(uint256 _allocPoint, address _distributor) public onlyOwner {
        require(distributors[_distributor].existance == true, "NONEXISTENT");
        updateAll();

        totalAllocPoint = totalAllocPoint
            .sub(distributors[_distributor].allocPoint)
            .add(_allocPoint);
        distributors[_distributor].allocPoint = _allocPoint;
    }

    /**
     * @notice Internal function to compare two number and return the smaller one
     */
    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a <= b) {
            return a;
        } else {
            return b;
        }
    }

    /**
     * @notice Internal function that returns 0 for subtraction overflows
     */
    function safeSub(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a <= b) {
            return 0;
        } else {
            uint256 c = a - b;
            return c;
        }
    }
}

